import React from 'react';
import MyChart from './MyChart';

const data = {
  labels: ['January', 'February', 'March'],
  datasets: [
    {
      label: 'Sales',
      data: [300, 400, 500],
      backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
    },
  ],
};

const options = {
  responsive: true,
  plugins: {
    legend: {
      position: 'top',
    },
    title: {
      display: true,
      text: 'Monthly Sales',
    },
  },
};

const App = () => {
  return (
    <div>
      <h1>Chart.js in React</h1>
      <MyChart data={data} options={options} />
    </div>
  );
};

export default App;
