import React, { useRef, useEffect } from 'react';
import { Chart, RadarController, RadarElement, CategoryScale, Title, Tooltip, Legend } from 'chart.js';

Chart.register(RadarController, RadarElement, CategoryScale, Title, Tooltip, Legend);

const BreachAlertChart = ({ breachAlertData }) => {
  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  const data = {
    labels: ['Alert Status'],
    datasets: [
      {
        label: 'Breach Alert',
        data: [breachAlertData],
        backgroundColor: 'rgba(255, 159, 64, 0.4)',
        borderColor: 'rgba(255, 159, 64, 1)',
        borderWidth: 2,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Breach Alert Status',
      },
    },
    scales: {
      r: {
        angleLines: {
          display: false,
        },
        ticks: {
          min: 0,
          max: 1,
          stepSize: 1,
          display: false,
        },
        grid: {
          color: 'rgba(200, 200, 200, 0.2)',
        },
      },
    },
  };

  useEffect(() => {
    const canvas = chartRef.current;

    if (chartInstance.current) {
      chartInstance.current.destroy();
    }

    chartInstance.current = new Chart(canvas, {
      type: 'radar',
      data: data,
      options: options,
    });

    return () => {
      if (chartInstance.current) {
        chartInstance.current.destroy();
      }
    };
  }, [breachAlertData]);

  return (
    <div className="chart-card">
      <canvas ref={chartRef} />
    </div>
  );
};

export default BreachAlertChart;
